INSERT INTO `xd_news` VALUES (11, 9, 'Register Guild', 'Guild', '<center><p><strong>ลงทะเบียน กิลด์</strong></p>\r\n\r\n<p>เงื่อนไข สมาชิกกิลด์ 6 คนขึ้นไป</p>\r\n\r\n<p>ส่งรูป LOGO กิลด์ พร้อม กับ แจ้งจำนวนสมาชิก ทีมงานจะส่งหินกิลให้ </p>\r\n\r\n<p><strong>( ปิดรับสมัคร ) วันเสาร์ที่ 3 เมษายน </strong></p>\r\n\r\n<p> </p>\r\n\r\n<p><strong>รางวัล</strong></p>\r\n\r\n<p><strong>30,000 Cash - จะได้รับ รางวัล เมื่อ สมาชิกกิลด์ เลเวล 99 ครบทั้ง 6 คน</strong></p></center>\r\n', 'homun-thumb-Recovered.png', '2021-03-30 16:29:44');
